def encode_shift(s: str):
    """
    returns encoded string by shifting every character by 5 in the alphabet.
    """
    return "".join([chr(((ord(ch) + 5 - ord("a")) % 26) + ord("a")) for ch in s])


def decode_shift(s: str):
    """
    takes as input string encoded with encode_shift function. Returns decoded string.
    """
    return "".join([chr(((ord(ch) - 5 - ord("a")) % 26) + ord("a")) for ch in s])

def search(lst):
    '''
    You are given a non-empty list of positive integers. Return the greatest integer that is greater than 
    zero, and has a frequency greater than or equal to the value of the integer itself. 
    The frequency of an integer is the number of times it appears in the list.
    If no such a value exist, return -1.
    Examples:
        search([4, 1, 2, 2, 3, 1]) == 2
        search([1, 2, 2, 3, 3, 3, 4, 4, 4]) == 3
        search([5, 5, 4, 4, 4]) == -1
    '''
    # Create a dictionary to store the frequency of each integer
    freq_dict = {}
    for num in lst:
        if num > 0:
            if num in freq_dict:
                freq_dict[num] += 1
            else:
                freq_dict[num] = 1

    # Find the greatest integer that satisfies the condition
    max_num = -1
    for num, freq in freq_dict.items():
        if freq >= num:
            max_num = max(max_num, num)

    return max_num

def is_prime_hex_digit(digit):
    """Helper function to determine if a hexadecimal digit is prime."""
    hex_primes = {'2', '3', '5', '7', 'B', 'D'}
    return str(int(digit, 16)) in hex_primes

def hex_key(num):
    """Counts the number of hexadecimal digits that are primes."""
    if not num:
        return 0

    # Count primes using list comprehension
    prime_count = sum(is_prime_hex_digit(digit) for digit in num)

    return prime_count

def sum_of_digits(n):
    """Helper function to sum digits of a number."""
    return sum(int(digit) for digit in str(n))

def to_binary(n):
    """Helper function to convert an integer to its binary representation."""
    return bin(n)[2:]

def solve(N):
    """Given a positive integer N, return the total sum of its digits in binary."""
    # Validate input
    if not isinstance(N, int) or not (0 <= N <= 10000):
        raise ValueError("Input must be an integer within the range 0 to 10000.")

    # Convert N to binary
    binary_representation = to_binary(N)

    # Sum the digits of the binary representation
    sum_of_binary_digits = sum_of_digits(binary_representation)

    # Convert the sum back to binary
    return to_binary(sum_of_binary_digits)

def swap_case(char):
    """
    Swap the case of a single character.
    """
    if char.isupper():
        return char.lower()
    else:
        return char.upper()

def encode(message):
    """
    Encode a message by swapping the case of all letters and replacing vowels with the letter that appears 2 places ahead in the alphabet.
    """
    # Step 1: Define a dictionary to map each vowel to its "two places ahead" counterpart in the alphabet
    vowel_map = {'a': 'c', 'e': 'g', 'i': 'k', 'o': 'q', 'u': 'w'}

    # Step 2: Initialize an empty list to store the encoded message
    encoded_message = []

    # Step 3: Iterate over each character in the message
    for char in message:
        # Check if the character is a vowel
        if char.lower() in vowel_map:
            # Replace it with its mapped value
            encoded_char = vowel_map[char.lower()]
        else:
            # Apply the case swap function
            encoded_char = swap_case(char)

        # Append the transformed character to the encoded message list
        encoded_message.append(encoded_char)

    # Step 4: Join the list into a single string and return it
    return ''.join(encoded_message)

def is_prime(n):
    """Helper function to check for primality"""
    if n == 2:
        return True
    if n % 2 == 0 or n == 1:
        return False
    for i in range(3, int(n**0.5) + 1, 2):
        if n % i == 0:
            return False
    return True

def count_up_to(n):
    """Returns an array of the first n prime numbers less than n"""
    primes = []
    for i in range(2, n):
        if is_prime(i):
            primes.append(i)
            if len(primes) == n:
                break
    return primes

def is_palindrome(n):
    """
    Helper function to check if a number is a palindrome.
    """
    return str(n) == str(n)[::-1]

def even_odd_palindrome(n):
    """
    Given a positive integer n, return a tuple that has the number of even and odd
    integer palindromes that fall within the range(1, n), inclusive.
    """
    even_count = 0
    odd_count = 0

    for i in range(1, n + 1):
        if is_palindrome(i):
            if i % 2 == 0:
                even_count += 1
            else:
                odd_count += 1

    return (even_count, odd_count)

def histogram(test):
    """Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.

    Example:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}



    Action Plan:
    1. Initialize the input string and create a list of unique letters
        - Split the string into a list using the space-separated delimiter

    2. Create an empty dictionary to hold letter occurrences

    3. Iterate over each letter in the list:
        - Check if the letter is already in the dictionary; if not, add it
        - Increment the letter's occurrence count

    4. Determine the maximum occurrence:
        - Iterate through the dictionary to find the max_count

    5. Collect the letters with max_count:
        - Create an empty list to store the result
        - Iterate over the dictionary items, add letters with max_count to the result list

    6. Handle edge cases:
        - Return an empty dictionary for an empty string
        - For strings with only one unique character, return a dictionary with that character as the key and count as 1
        - Handle strings with multiple identical characters correctly

    7. Return the dictionary with letters and their corresponding counts"""

    # Step 1: Initialize the input string and create a list of unique letters
    letters = test.split()

    # Step 2: Create an empty dictionary to hold letter occurrences
    letter_counts = {}

    # Step 3: Iterate over each letter in the list
    for letter in letters:
        if letter not in letter_counts:
            letter_counts[letter] = 1
        else:
            letter_counts[letter] += 1

    # Step 4: Determine the maximum occurrence
    max_count = max(letter_counts.values())

    # Step 5: Collect the letters with max_count
    result = [letter for letter, count in letter_counts.items() if count == max_count]

    # Step 6: Handle edge cases
    if not letters:
        return {}
    elif len(set(letters)) == 1:
        return {letters[0]: 1}

    # Step 7: Return the dictionary with letters and their corresponding counts
    return {letter: max_count for letter in result}

def split_words(txt):
    '''
    Given a string of words, return a list of words split on whitespace, if no whitespaces exists in the text you
    should split on commas ',' if no commas exists you should return the number of lower-case letters with odd order in the
    alphabet, ord('a') = 0, ord('b') = 1, ... ord('z') = 25
    Examples
    split_words("Hello world!") ➞ ["Hello", "world!"]
    split_words("Hello,world!") ➞ ["Hello", "world!"]
    split_words("abcdef") == 3 
    '''
    # Check if the text contains whitespace
    if ' ' in txt:
        return txt.split()
    # Check if the text contains comma
    elif ',' in txt:
        return txt.split(',')
    else:
        # Count the number of lower-case letters with odd order in the alphabet
        count = 0
        for char in txt:
            if char.islower() and ord(char) % 2 == 1:
                count += 1
        return count

